
async function timeCounter(cb) {
  console.log('start...')
  const start = new Date().getTime();
  typeof cb === 'function' && await cb();
  console.log(`用时: ${new Date().getTime() - start}ms`);
}

function sorterTest(sorter) {
  const temp = [3,6,4,8,2,76,5,46,567,65,8,23,5,4,34,567,867,23,43,564,6,2,4,56,6,8,54,3,23,5,6,87,75,34,43,34,34,65,5,3,2,5,5,54,7,1,1,78,678,354,345,42,6,75,3,43,345,34,25,];
  const array = [];
  for(let i=0; i<100; i++) {
    array.push(...temp);
  }

  timeCounter(async () => {
    typeof sorter === 'function' && console.log(await sorter(array));
  })
}

module.exports = {
  timeCounter,
  sorterTest,
};